mapper.select:
select

,

from oauth_access_token
where authentication_id = #{authenticationId,jdbcType=VARCHAR}


    AND authentication_id = #{authenticationId}


    AND token_id = #{tokenId}


    AND user_name = #{userName}

mapper.delete:
delete from oauth_access_token


    AND authentication_id = #{authenticationId}


    AND token_id = #{tokenId}
