mapper.select:
select 

from new


    AND new_id = #{newId}


    AND parent_id = #{parentId}


    AND open_id = #{openId}


    AND createTime >= #{beginTime}


    AND createTime <=#{endTime}

mapper.delete:
delete from new


    AND new_id = #{newId}


    AND parent_id = #{parentId}


    AND open_id = #{openId}
